import { useState } from "react";
import { useQuery, useMutation } from "@tanstack/react-query";
import { Button } from "@/components/ui/button";
import { GameCard } from "@/components/GameCard";
import { FilterPanel, FilterState } from "@/components/FilterPanel";
import { CreateGameModal, GameSettings } from "@/components/CreateGameModal";
import { BottomNavigation } from "@/components/BottomNavigation";
import { CurrencyDisplay } from "@/components/CurrencyDisplay";
import { useToast } from "@/hooks/use-toast";
import { Filter, Flag } from "lucide-react";
import { Game } from "@shared/schema";
import { useLocation } from "wouter";
import { apiRequest, queryClient } from "@/lib/queryClient";

export default function Lobby() {
  const [, setLocation] = useLocation();
  const { toast } = useToast();
  const [activeTab, setActiveTab] = useState<"profile" | "open" | "private" | "create">("open");
  const [isFilterOpen, setIsFilterOpen] = useState(false);
  const [isCreateOpen, setIsCreateOpen] = useState(false);
  const [userBalance] = useState(500);
  const [filters, setFilters] = useState<FilterState>({
    stakeRange: [100, 10000000],
    playerCounts: [2, 3, 4, 5, 6],
    deckSizes: [24, 36, 52],
  });

  const { data: games, isLoading } = useQuery<Game[]>({
    queryKey: ["/api/games"],
  });

  const createGameMutation = useMutation({
    mutationFn: async (settings: GameSettings) => {
      const response = await apiRequest("POST", "/api/games", settings);
      return await response.json();
    },
    onSuccess: (newGame) => {
      queryClient.invalidateQueries({ queryKey: ["/api/games"] });
      setLocation(`/game/${newGame.id}`);
    },
    onError: () => {
      toast({
        variant: "destructive",
        title: "Ошибка",
        description: "Не удалось создать игру",
      });
    },
  });

  const handleCreateGame = (settings: GameSettings) => {
    if (settings.stake > userBalance) {
      toast({
        variant: "destructive",
        title: "Недостаточно монет",
        description: `У вас ${userBalance} монет, а ставка ${settings.stake}`,
      });
      return;
    }
    createGameMutation.mutate(settings);
  };

  const handleJoinGame = (game: Game) => {
    if (game.stake > userBalance) {
      toast({
        variant: "destructive",
        title: "Недостаточно монет",
        description: `У вас ${userBalance} монет, а ставка ${game.stake}`,
      });
      return;
    }
    setLocation(`/game/${game.id}`);
  };

  const handleTabChange = (tab: "profile" | "open" | "private" | "create") => {
    if (tab === "create") {
      setIsCreateOpen(true);
    } else {
      setActiveTab(tab);
    }
  };

  return (
    <div className="min-h-screen bg-blue-texture pb-20">
      <div className="sticky top-0 z-30 bg-durak-blue/95 backdrop-blur-sm border-b border-durak-blue-dark shadow-lg">
        <div className="container mx-auto px-4 py-3 flex items-center justify-between">
          <div className="flex items-center gap-3">
            <Flag className="w-6 h-6 text-white" />
            <h1 className="text-xl font-bold text-white">Дурак Онлайн</h1>
          </div>

          <div className="flex items-center gap-3">
            <CurrencyDisplay amount={userBalance} />
            <Button
              variant="ghost"
              size="icon"
              onClick={() => setIsFilterOpen(true)}
              data-testid="button-open-filters"
              className="text-white hover:bg-white/20"
            >
              <Filter className="w-5 h-5" />
            </Button>
          </div>
        </div>
      </div>

      <div className="container mx-auto px-4 py-6">
        {activeTab === "open" && (
          <>
            <div className="mb-4">
              <h2 className="text-lg font-bold text-white mb-2">Открытые игры</h2>
              <p className="text-sm text-white/80">
                Выберите игру или создайте свою
              </p>
            </div>

            {isLoading ? (
              <div className="grid grid-cols-1 sm:grid-cols-2 lg:grid-cols-3 gap-4">
                {[1, 2, 3, 4, 5, 6].map((i) => (
                  <div
                    key={i}
                    className="h-32 bg-white/20 rounded-lg animate-pulse"
                  />
                ))}
              </div>
            ) : games && games.length > 0 ? (
              <div className="grid grid-cols-1 sm:grid-cols-2 lg:grid-cols-3 gap-4">
                {games.map((game) => (
                  <GameCard
                    key={game.id}
                    game={game}
                    onClick={() => handleJoinGame(game)}
                  />
                ))}
              </div>
            ) : (
              <div className="text-center py-12">
                <p className="text-white/80 mb-4">Нет доступных игр</p>
                <Button
                  onClick={() => setIsCreateOpen(true)}
                  data-testid="button-create-first-game"
                >
                  Создать игру
                </Button>
              </div>
            )}
          </>
        )}

        {activeTab === "profile" && (
          <div className="text-center py-12">
            <h2 className="text-2xl font-bold text-white mb-4">Профиль</h2>
            <p className="text-white/80">Профиль в разработке</p>
          </div>
        )}

        {activeTab === "private" && (
          <div className="text-center py-12">
            <h2 className="text-2xl font-bold text-white mb-4">Приватные игры</h2>
            <p className="text-white/80">Приватные игры в разработке</p>
          </div>
        )}
      </div>

      <FilterPanel
        isOpen={isFilterOpen}
        onClose={() => setIsFilterOpen(false)}
        onApply={setFilters}
        initialFilters={filters}
      />

      <CreateGameModal
        isOpen={isCreateOpen}
        onClose={() => setIsCreateOpen(false)}
        onCreate={handleCreateGame}
      />

      <BottomNavigation activeTab={activeTab} onTabChange={handleTabChange} />
    </div>
  );
}

client/src/hooks/useWebSocket.tsx.content:
import { useEffect, useRef, useState, useCallback } from 'react';
import type { GameState } from '@shared/schema';

interface WebSocketMessage {
  type: string;
  payload: any;
}

export function useWebSocket(gameId?: string) {
  const [isConnected, setIsConnected] = useState(false);
  const [lastMessage, setLastMessage] = useState<WebSocketMessage | null>(null);
  const wsRef = useRef<WebSocket | null>(null);
  const reconnectTimeoutRef = useRef<NodeJS.Timeout>();
  const reconnectAttemptsRef = useRef(0);
  const isManualCloseRef = useRef(false);
  const gameIdRef = useRef(gameId);

  // Update gameId ref when it changes
  useEffect(() => {
    gameIdRef.current = gameId;
  }, [gameId]);

  const connect = useCallback(() => {
    const currentGameId = gameIdRef.current;
    if (!currentGameId) return;

    // Prevent multiple connections
    if (wsRef.current?.readyState === WebSocket.OPEN || wsRef.current?.readyState === WebSocket.CONNECTING) {
      return;
    }

    // Clear any existing reconnect timeout
    if (reconnectTimeoutRef.current) {
      clearTimeout(reconnectTimeoutRef.current);
      reconnectTimeoutRef.current = undefined;
    }

    try {
      // Build WebSocket URL - use the same domain/protocol as the page
      // In Replit, this automatically routes to the correct backend
      const protocol = window.location.protocol === 'https:' ? 'wss:' : 'ws:';
      const hostname = window.location.hostname;
      const port = window.location.port;

      // Construct URL properly - include port if it's not default HTTP/HTTPS
      let wsUrl: string;
      if (port && port !== '80' && port !== '443') {
        wsUrl = `${protocol}//${hostname}:${port}/ws`;
      } else {
        wsUrl = `${protocol}//${hostname}/ws`;
      }

      console.log('Connecting to WebSocket:', wsUrl);
      const ws = new WebSocket(wsUrl);
      wsRef.current = ws;
      isManualCloseRef.current = false;

      ws.onopen = () => {
        console.log('WebSocket connected successfully');
        setIsConnected(true);
        reconnectAttemptsRef.current = 0;
      };

      ws.onmessage = (event) => {
        try {
          const message = JSON.parse(event.data);
          setLastMessage(message);
        } catch (error) {
          console.error('Failed to parse WebSocket message:', error);
        }
      };

      ws.onclose = (event) => {
        console.log('WebSocket disconnected', event.code, event.reason);
        setIsConnected(false);
        wsRef.current = null;

        // Only reconnect if it wasn't a manual close and we still have a gameId
        if (!isManualCloseRef.current && gameIdRef.current && reconnectAttemptsRef.current < 5) {
          reconnectAttemptsRef.current += 1;
          const delay = Math.min(1000 * Math.pow(2, reconnectAttemptsRef.current), 10000);

          reconnectTimeoutRef.current = setTimeout(() => {
            if (gameIdRef.current && !isManualCloseRef.current) {
              console.log(`Attempting to reconnect... (attempt ${reconnectAttemptsRef.current})`);
              connect();
            }
          }, delay);
        } else if (reconnectAttemptsRef.current >= 5) {
          console.error('Max reconnection attempts reached');
        }
      };

      ws.onerror = (error) => {
        console.error('WebSocket error:', error);
      };
    } catch (error) {
      console.error('Failed to create WebSocket:', error);
    }
  }, []);

  const send = useCallback((type: string, payload: any) => {
    if (wsRef.current?.readyState === WebSocket.OPEN) {
      wsRef.current.send(JSON.stringify({ type, payload }));
    } else {
      console.warn('WebSocket not connected, unable to send:', type);
    }
  }, []);

  const disconnect = useCallback(() => {
    isManualCloseRef.current = true;
    reconnectAttemptsRef.current = 0;

    if (reconnectTimeoutRef.current) {
      clearTimeout(reconnectTimeoutRef.current);
      reconnectTimeoutRef.current = undefined;
    }

    if (wsRef.current) {
      wsRef.current.onclose = null;
      wsRef.current.onerror = null;
      wsRef.current.onmessage = null;
      wsRef.current.onopen = null;

      if (wsRef.current.readyState === WebSocket.OPEN || wsRef.current.readyState === WebSocket.CONNECTING) {
        wsRef.current.close();
      }
      wsRef.current = null;
    }

    setIsConnected(false);
    setLastMessage(null);
  }, []);

  useEffect(() => {
    connect();

    return () => {
      // Cleanup on unmount or gameId change
      if (wsRef.current) {
        // Don't disconnect on dependency change, just let it reconnect if needed
      }
    };
  }, [gameId, connect]);

  return { isConnected, lastMessage, send, disconnect };
}

interface Player {
  userId: string;
  username: string;
}

export function useGameEvents(gameId: string, userId: string, username: string) {
  const { isConnected, lastMessage, send, disconnect } = useWebSocket(gameId);
  const [gameState, setGameState] = useState<GameState | null>(null);
  const [players, setPlayers] = useState<Player[]>([]);
  const [chatMessages, setChatMessages] = useState<any[]>([]);

  // Join game when connected
  useEffect(() => {
    if (isConnected && gameId) {
      send('join_game', { gameId, userId, username });
      console.log('Sent join_game message');
    }
  }, [isConnected, gameId, userId, username, send]);

  const lastMessageRef = useRef<string>('');

  useEffect(() => {
    if (!lastMessage) return;

    // Prevent processing the same message twice
    const messageStr = JSON.stringify(lastMessage);
    if (lastMessageRef.current === messageStr) return;
    lastMessageRef.current = messageStr;

    const { type, payload } = lastMessage;

    switch (type) {
      case 'player_joined':
        setPlayers((prev) => {
          if (prev.some((p) => p.userId === payload.userId)) {
            return prev;
          }
          return [...prev, { userId: payload.userId, username: payload.username }];
        });
        break;

      case 'player_left':
        setPlayers((prev) => prev.filter((p) => p.userId !== payload.userId));
        break;

      case 'game_started':
        setGameState(payload);
        if (payload.players && Array.isArray(payload.players)) {
          setPlayers(payload.players.map((p: any) => ({ userId: p.id, username: p.username })));
        }
        break;

      case 'game_update':
        setGameState(payload);
        if (payload.players && Array.isArray(payload.players)) {
          setPlayers(payload.players.map((p: any) => ({ userId: p.id, username: p.username })));
        }
        break;

      case 'chat_message':
        setChatMessages((prev) => [...prev, payload]);
        break;
    }
  }, [lastMessage]);

  const sendChatMessage = useCallback((message: string) => {
    send('chat_message', { gameId, userId, username, message });
  }, [send, gameId, userId, username]);

  const attack = useCallback((playerId: string, card: any) => {
    send('attack', { gameId, playerId, card });
  }, [send, gameId]);

  const defend = useCallback((playerId: string, card: any, tableCardIndex: number) => {
    send('defend', { gameId, playerId, card, tableCardIndex });
  }, [send, gameId]);

  const take = useCallback((playerId: string) => {
    send('take', { gameId, playerId });
  }, [send, gameId]);

  const beat = useCallback((playerId: string) => {
    send('beat', { gameId, playerId });
  }, [send, gameId]);

  return {
    isConnected,
    gameState,
    players,
    chatMessages,
    sendChatMessage,
    attack,
    defend,
    take,
    beat,
    disconnect,
  };
}

client/src/components/BottomNavigation.t.content:
import { User, Heart, Lock, Plus } from "lucide-react";
import { cn } from "@/lib/utils";

interface BottomNavigationProps {
  activeTab: "profile" | "open" | "private" | "create";
  onTabChange: (tab: "profile" | "open" | "private" | "create") => void;
}

const tabs = [
  { id: "profile" as const, label: "Профиль", icon: User },
  { id: "open" as const, label: "Открытые", icon: Heart },
  { id: "private" as const, label: "Приватные", icon: Lock },
  { id: "create" as const, label: "Создать игру", icon: Plus },
];

export function BottomNavigation({ activeTab, onTabChange }: BottomNavigationProps) {
  return (
    <div className="fixed bottom-0 left-0 right-0 bg-white border-t shadow-lg z-40">
      <div className="flex items-center justify-around">
        {tabs.map((tab) => {
          const Icon = tab.icon;
          const isActive = activeTab === tab.id;

          return (
            <button
              key={tab.id}
              onClick={() => onTabChange(tab.id)}
              data-testid={`tab-${tab.id}`}
              className={cn(
                "flex-1 flex flex-col items-center justify-center gap-1 py-3 px-2 transition-colors",
                "hover-elevate active-elevate-2",
                isActive
                  ? "text-durak-red bg-red-50"
                  : "text-muted-foreground"
              )}
            >
              <Icon className="w-5 h-5" />
              <span className="text-xs font-medium">{tab.label}</span>
            </button>
          );
        })}
      </div>
    </div>
  );
}

