@layer base {
  * {
    @apply border-border;
  }

  body {
    @apply font-sans antialiased bg-background text-foreground;
  }
}

/**
 * Using the elevate system.
 * Automatic contrast adjustment.
 *
 * <element className="hover-elevate" />
 * <element className="active-elevate-2" />
 *
 * // Using the tailwind utility when a data attribute is "on"
 * <element className="toggle-elevate data-[state=on]:toggle-elevated" />
 * // Or manually controlling the toggle state
 * <element className="toggle-elevate toggle-elevated" />
 *
 * Elevation systems have to handle many states.
 * - not-hovered, vs. hovered vs. active  (three mutually exclusive states)
 * - toggled or not
 * - focused or not (this is not handled with these utilities)
 *
 * Even without handling focused or not, this is six possible combinations that
 * need to be distinguished from eachother visually.
 */
@layer utilities {

  /* Hide ugly search cancel button in Chrome until we can style it properly */
  input[type="search"]::-webkit-search-cancel-button {
    @apply hidden;
  }

  /* Placeholder styling for contentEditable div */
  [contenteditable][data-placeholder]:empty::before {
    content: attr(data-placeholder);
    color: hsl(var(--muted-foreground));
    pointer-events: none;
  }

  /* .no-default-hover-elevate/no-default-active-elevate is an escape hatch so consumers of
   * buttons/badges can remove the automatic brightness adjustment on interactions
   * and program their own. */
  .no-default-hover-elevate {}

  .no-default-active-elevate {}


  /**
   * Toggleable backgrounds go behind the content. Hoverable/active goes on top.
   * This way they can stack/compound. Both will overlap the parent's borders!
   * So borders will be automatically adjusted both on toggle, and hover/active,
   * and they will be compounded.
   */
  .toggle-elevate::before,
  .toggle-elevate-2::before {
    content: "";
    pointer-events: none;
    position: absolute;
    inset: 0px;
    /*border-radius: inherit;   match rounded corners */
    border-radius: inherit;
    z-index: -1;
    /* sits behind content but above backdrop */
  }

  .toggle-elevate.toggle-elevated::before {
    background-color: var(--elevate-2);
  }

  /* If there's a 1px border, adjust the inset so that it covers that parent's border */
  .border.toggle-elevate::before {
    inset: -1px;
  }

  /* Does not work on elements with overflow:hidden! */
  .hover-elevate:not(.no-default-hover-elevate),
  .active-elevate:not(.no-default-active-elevate),
  .hover-elevate-2:not(.no-default-hover-elevate),
  .active-elevate-2:not(.no-default-active-elevate) {
    position: relative;
    z-index: 0;
  }

  .hover-elevate:not(.no-default-hover-elevate)::after,
  .active-elevate:not(.no-default-active-elevate)::after,
  .hover-elevate-2:not(.no-default-hover-elevate)::after,
  .active-elevate-2:not(.no-default-active-elevate)::after {
    content: "";
    pointer-events: none;
    position: absolute;
    inset: 0px;
    /*border-radius: inherit;   match rounded corners */
    border-radius: inherit;
    z-index: 999;
    /* sits in front of content */
  }

  .hover-elevate:hover:not(.no-default-hover-elevate)::after,
  .active-elevate:active:not(.no-default-active-elevate)::after {
    background-color: var(--elevate-1);
  }

  .hover-elevate-2:hover:not(.no-default-hover-elevate)::after,
  .active-elevate-2:active:not(.no-default-active-elevate)::after {
    background-color: var(--elevate-2);
  }

  /* If there's a 1px border, adjust the inset so that it covers that parent's border */
  .border.hover-elevate:not(.no-hover-interaction-elevate)::after,
  .border.active-elevate:not(.no-active-interaction-elevate)::after,
  .border.hover-elevate-2:not(.no-hover-interaction-elevate)::after,
  .border.active-elevate-2:not(.no-active-interaction-elevate)::after,
  .border.hover-elevate:not(.no-hover-interaction-elevate)::after {
    inset: -1px;
  }

  /* ========== RST GAMES DURAK CUSTOM UTILITIES ========== */

  /* Game table background texture */
  .game-table-bg {
    background-color: hsl(var(--rst-game-bg));
    background-image: 
      radial-gradient(circle at 20% 50%, rgba(255,255,255,.02) 0%, transparent 50%),
      radial-gradient(circle at 80% 50%, rgba(0,0,0,.02) 0%, transparent 50%);
  }

  /* Playing card styling */
  .playing-card {
    aspect-ratio: 5 / 7;
    border-radius: 8px;
    box-shadow: 0 2px 8px rgba(0, 0, 0, 0.3);
    background: white;
    transition: transform 0.2s ease-out;
  }

  .playing-card:hover {
    transform: translateY(-10px);
  }

  .playing-card-back {
    background: linear-gradient(135deg, #c41e3a 0%, #8b0000 100%);
    background-image:
      repeating-linear-gradient(45deg, transparent, transparent 10px, rgba(255,255,255,.05) 10px, rgba(255,255,255,.05) 20px),
      linear-gradient(135deg, #c41e3a 0%, #8b0000 100%);
  }

  /* RST action button (white bg, red text) */
  .rst-action-btn {
    background: white;
    color: hsl(var(--rst-red));
    font-weight: 600;
    border-radius: 25px;
    box-shadow: 0 3px 6px rgba(0, 0, 0, 0.2);
    transition: all 0.2s;
  }

  .rst-action-btn:hover {
    transform: translateY(-2px);
    box-shadow: 0 4px 8px rgba(0, 0, 0, 0.3);
  }

  .rst-action-btn:active {
    transform: scale(0.98);
  }

  /* Toggle button with checkmark */
  .rst-toggle-btn {
    position: relative;
    background: white;
    border-radius: 20px;
    padding: 8px 16px;
    border: 2px solid transparent;
    transition: all 0.2s;
  }

  .rst-toggle-btn.selected {
    border-color: hsl(var(--rst-red));
  }

  .rst-toggle-btn.selected::after {
    content: "✓";
    position: absolute;
    top: -6px;
    right: -6px;
    background: hsl(var(--rst-red));
    color: white;
    width: 20px;
    height: 20px;
    border-radius: 50%;
    display: flex;
    align-items: center;
    justify-content: center;
    font-size: 12px;
    font-weight: bold;
  }

  /* Money animation */
  @keyframes money-win {
    0% {
      opacity: 0;
      transform: translate(-50%, -50%) scale(0.5);
    }
    20% {
      opacity: 1;
      transform: translate(-50%, -50%) scale(1.2);
    }
    80% {
      opacity: 1;
      transform: translate(-50%, -80px) scale(1);
    }
    100% {
      opacity: 0;
      transform: translate(-50%, -120px) scale(0.8);
    }
  }

  .money-win-animation {
    animation: money-win 2.5s ease-out forwards;
  }

  .money-bill {
    background: linear-gradient(135deg, #b8d89e 0%, #a8d08d 50%, #98c07d 100%);
    border: 2px solid #7fa86a;
    border-radius: 8px;
    box-shadow: 
      0 4px 8px rgba(0, 0, 0, 0.2),
      inset 0 1px 0 rgba(255, 255, 255, 0.3);
  }

  /* Card deal animation */
  @keyframes deal-card {
    from {
      transform: translate(0, 0) rotate(0deg);
      opacity: 0;
    }
    to {
      transform: translate(var(--deal-x), var(--deal-y)) rotate(var(--deal-rot));
      opacity: 1;
    }
  }

  .card-deal {
    animation: deal-card 0.3s ease-out forwards;
  }

  /* Card play animation */
  @keyframes play-card {
    to {
      transform: translate(var(--play-x), var(--play-y));
    }
  }

  .card-play {
    animation: play-card 0.4s ease-out forwards;
  }

  /* Script font for section headers */
  .font-script {
    font-family: var(--font-script);
    font-style: italic;
    font-weight: 600;
  }

  /* Responsive card sizing */
  @media (max-width: 480px) {
    .playing-card {
      width: clamp(40px, 15vw, 60px);
    }
  }

  @media (min-width: 481px) and (max-width: 812px) and (orientation: landscape) {
    .playing-card {
      width: clamp(45px, 10vw, 70px);
    }
  }

  @media (min-width: 813px) {
    .playing-card {
      width: clamp(60px, 12vw, 80px);
    }
  }

  /* Bottom navigation active state */
  .bottom-nav-item {
    transition: color 0.2s;
  }

  .bottom-nav-item.active {
    color: hsl(var(--rst-red));
  }

  /* Lobby item styling */
  .lobby-item {
    background: hsl(var(--rst-lobby-item) / 0.6);
    border: 1px solid hsl(var(--border));
    border-radius: 8px;
    transition: background-color 0.2s;
  }

  .lobby-item:hover {
    background: hsl(var(--rst-lobby-item) / 0.8);
  }

  /* Circular timer progress */
  @keyframes timer-progress {
    from {
      stroke-dashoffset: 0;
    }
    to {
      stroke-dashoffset: var(--timer-dashoffset);
    }
  }

  .timer-circle {
    animation: timer-progress var(--timer-duration) linear forwards;
  }
}

